 *
 * This class was generated by the Harmony / Don't modify this class it will be regenerated by Harmony !
 */
class PokeAreneTypeBase extends AbstractType
{

    /**
     * @param FormBuilderInterface $builder
     * @param array $options
     */
    public function buildForm(FormBuilderInterface $builder, array $options)
    {
        $builder
            ->add('nom')
            ->add('zone', 'entity', array(
                'class' => 'PokebattleApiBundle:PokeZone',
                'property' => 'id',
                'required' => false))
            ->add('position', 'entity', array(
                'class' => 'PokebattleApiBundle:PokePosition',
                'property' => 'id',
                'required' => false))
            
            
        ;
    }

    /**
     * @param OptionsResolverInterface $resolver
     */
    public function setDefaultOptions(OptionsResolverInterface $resolver)
    {
        $resolver->setDefaults(array(
            'data_class' => 'Pokebattle\ApiBundle\Entity\PokeArene',
            'csrf_protection' => false
        ));
    }

    /**
     * @return string
     */
    public function getName()
    {
        return '';
    }
}
